    def convert_sec(self, lnth):
        lnth = lnth/1000
        try:
            if int(lnth-(60*(lnth//60))) < 10:
                return("{}:0{}".format(int(lnth//60), int(lnth-(60*(lnth//60)))))
            else:
                return("{}:{}".format(int(lnth//60), int(lnth-(60*(lnth//60)))))
        except:
            print('Error: Length is {}'.format(lnth))
            
