    def prepare(self, link):
        print('preparing')
        try:
            
            self.sound = MediaPlayer()
            self.sound.setDataSource(link)
            self.sound.setAudioStreamType(AudioManager.STREAM_NOTIFICATION)
            self.sound.prepare()
            self.sound.setLooping(False)
            
        except Exception as e:
            print(e)
            time.sleep(0.25)
            self.prepare(link)
        print('prepared')
        
